      <div
        className={`flex items-center space-x-1 sm:space-x-2 px-3 sm:px-4 py-2 sm:py-2.5 bg-gray-900/50 rounded-xl text-xs sm:text-sm font-medium hover:bg-gradient-to-r transition-all duration-200 cursor-pointer hover:scale-105 border border-gray-800/50 hover:border-gray-600/50`}
        style={{ color }}
      >
        {iconMap[name] || <div className="w-4 h-4 sm:w-5 sm:h-5 bg-current rounded"></div>}
        <span className="text-xs sm:text-sm font-medium">{name}</span>
      </div>
